package com.cui.phs.exception;public class ServiceException extends Exception {    private static final long serialVersionUID = 1L;    private String code;    private String msg;    public ServiceException(String code, String msg) {        super(msg);        this.code = code;        this.msg = msg;    }    public String getCode() {        return this.code;    }    public void setCode(String code) {        this.code = code;    }    public String getMsg() {        return this.msg;    }    public void setMsg(String msg) {        this.msg = msg;    }}